let signal = ['_','#','!','$'];
let cleanWords = words.replace(/[.,!?]/g, "").split(" ");

let typePassword = document.getElementById('strong')
let lengthPass = document.getElementById('lengthPass')
let SelectPassword = document.getElementById('selectTypePass')


let div = document.querySelector(".resetButton");
let btn = `<button onclick = "empty()">Reset</button>`;
//add value locastorage in input




function showPassword(btn){
    if(btn.innerHTML === "Show"){
        //add locastorage
        localStorage.setItem('showPass',"Hide");
        // ===========
        input.type = "text";
        btn.innerHTML = localStorage.getItem("showPass");
        
    }else{
        //add locastorage
        localStorage.setItem('showPass',"Show");
        // ===========
        input.type = "password";
        btn.innerHTML = localStorage.getItem("showPass");
    }
    //add value localstorage in input
}




if(localStorage.length > 0) {
    input.value = localStorage.getItem("date");
}
function runFunction(){
       div.innerHTML = btn;
   localStorage.setItem('date' , input.value)
   
   const w1 = Math.floor(Math.random() * cleanWords.length);
   const w2 = Math.floor(Math.random() * cleanWords.length);
   const w3 = Math.floor(Math.random() * signal.length);
   let num = 1e6;
   const randomNumbers = Math.floor(Math.random() * num) + 1;
   let randomWords ;

if(SelectPassword.value === "easy"){
    num = 1e2;
    randomWords=cleanWords[w1]
    +(randomNumbers.toString())
    +signal[w3];
    //uppercase && lowercase
    let resualt =  randomWords[1].toUpperCase() + randomWords ;
    input.value = resualt;

    lengthPass.innerHTML = input.value.length;

}else if(SelectPassword.value === "normal"){
    
    num = 1e3;
    randomWords=cleanWords[w1]
    +cleanWords[w2]
    +(randomNumbers.toString())
    +signal[w3];
    //uppercase && lowercase
    let resualt =  randomWords[1].toUpperCase() + randomWords ;
    input.value = resualt;

    lengthPass.innerHTML = input.value.length;

}else if(SelectPassword.value === "hard"){

    num = 1e5;
    randomWords=cleanWords[w1]
    +cleanWords[w2]
    +signal[w3]
    +signal[w3]
    +(randomNumbers.toString())+signal[w3];
    
    //uppercase && lowercase
    let resualt =  randomWords[1].toUpperCase() + randomWords ;
    input.value = resualt;


    lengthPass.innerHTML = input.value.length;

}else if(SelectPassword.value === "difficult"){
    num = 1e9;
     randomWords =  cleanWords[w1]
    +signal[w3] 
    +signal[w3]
    + cleanWords[w1]
    + cleanWords[w2]
    + cleanWords[w2%2]
    + randomNumbers.toString()+signal[w3];
    //uppercase && lowercase
    let resualt =  randomWords[1].toUpperCase() + randomWords ;
    input.value = resualt;


    lengthPass.innerHTML = input.value.length;

}

  
}



lengthPass.innerHTML = input.value.length;
SelectPassword.onchange = function () {
    typePassword.innerHTML = SelectPassword.value;
}




